mapper.select:
select case when exists(
select *
from ujcms_action t


        t.user_id_ = #{userId}
        or t.ip_ = #{ip}
        or t.cookie_ = #{cookie}


        and t.ref_type_ = #{refType}


        and t.ref_id_ = #{refId}


        and t.ref_option_ = #{refOption}


        and t.created_ > #{date}


) then 1
else 0
end as non_zero_exists

select

from ujcms_action t
where t.id_ = #{id,jdbcType=BIGINT}
