mapper.update:
update ujcms_performance_type
set order_ = order_ + 1
where order_ < #{from} and order_ >= #{to}

update ujcms_performance_type
set order_ = order_ - 1
where order_ > #{from} and order_ <= #{to}

update ujcms_performance_type
set order_ = #{order}
where id_ = #{id}

mapper.select:
