mapper.select:
SELECT t.name_ AS name_, SUM(t.pv_count_) AS pv_count_, SUM(t.uv_count_) AS uv_count_, SUM(t.ip_count_) AS ip_count_, SUM(t.nv_count_) AS nv_count_, SUM(t.bounce_count_) AS bounce_count_, SUM(t.duration_) AS duration_ FROM ujcms_visit_stat t WHERE t.site_id_ = #{siteId} AND t.type_ = #{type}
AND t.date_string_ >= #{begin}
AND t.date_string_ <= #{end}
GROUP BY t.name_ ORDER BY SUM(t.pv_count_) desc

mapper.delete:
delete
from ujcms_visit_stat
where date_string_ = #{dateString}
and type_ = #{type}

mapper.select:
select

from ujcms_visit_stat t
where t.id_ = #{id,jdbcType=INTEGER}
